import React, { Component } from 'react';
import { HorizontalBar } from 'react-chartjs-2';

class BarChart extends Component {


    state = {
        data: {
            datasets: [{
                barPercentage: 0.7,

                minBarLength: 2,
                data: [40.6, 35.3, 28.1, 26.7, 22.8],
                backgroundColor: [
                    'rgb(30,72,125)',
                    'rgb(47,85,151)',
                    'rgb(143,170,220)',
                    'rgb(180,199,231)',
                    'rgb(218,227,243)'
                ],
                borderWidth: 0
            }],

            // These labels appear in the legend and in the tooltips when hovering different arcs
            labels: [
                'Services financiers & assurance',
                'Production et distribution d\'électricité',
                'Édition',
                'Fabrication de produits informatiques',
                'Industrie chimique'
            ],
            

        }

    }

    // constructor(props) {
    //     super(props);

    //     this.state.chartData = props.chartData;
    // }
    render() {
        return (
            <div className="chart-container" style={{ position: 'relative', height: '100%', width: '100%' }}>
                <HorizontalBar

                    data={this.state.data}
                    width={'70%'}
                    height={'35%'}
                    options={{
                        maintainAspectRatio: false,

                        legend: {

                            display: false,
                            labels: {
                                // This more specific font property overrides the global property
                                fontColor: 'red',
                                boxWidth: 10,
                                padding: 20,
                                fontFamily: 'Helvetica Neue',

                            }
                        },

                        layout: {
                            padding: {
                                left: 0,
                                right: 0,
                                top: 20,
                                bottom: 40
                            }
                        },
                        scales: {
                            xAxes: [{
                                gridLines: {
                                    display:true,
                                    color:'rgb(217,217,217)',
                                    lineWidth:0.5,
                                    zeroLineWidth:0,
                                    zeroLineColor :'rgb(30,72,125)',
                                    drawBorder: false,
                                    borderDash:[5,5]
                                },
                                ticks: {
                                    fontColor: "#edf0f1",
                                    fontSize: 12,

                                    stepSize: 5,
                                    beginAtZero: true
                                }
                            }],
                            yAxes: [{
                                gridLines: {
                                    display:false,
                                    
                                } ,
                                ticks: {
                                    fontColor: "white",
                                    fontSize: 14,
                                    stepSize: 1,
                                    beginAtZero: true
                                }  
                            }]
                        }

                    }
                    }
                />
            </div>
        );
    }
}

export default BarChart;
