import React,{useState} from 'react';
import Header from './Header/Header'
import AppStyle from './App.css';
import Paper from './Filtres/FIltre'
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Box from './Box/Box';
import DoughnutChart from './Chart/DoughnutChart'
import Barchart from './Chart/BarChart'
import RatingBarCahrt from './Chart/RatingBarChart'
import MaturityBarChart from './Chart/MatrityBarCahrt' 
import CountrepartieTable from './Chart/ContrepartieTable'
import GeoChart from './Chart/GeoChart'
import Geodata from './Chart/custom.json'
import ScrRatingChart from './Chart/ScrCahrt'

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    maxWidth: '1500px',
    marginRight: 'auto',
    marginLeft: 'auto',
  },
  item: {
    minWidth: '300px',
  }
}));

/*------------------------creation données -------------------------*/
function createData(Entreprise, Exposition, Secteur) {
  return { Entreprise, Exposition, Secteur };
}

const rows = [
  createData('RECKITT BENCKISER GROUP PLC', '3.37%', 'Activités des services financiers'),
  createData('AIR LIQUIDE', '3.34%', 'Industrie chimique'),
  createData('DNCA FINANCE LUXEMBOURG', '3.24%', 'DNCA FINANCE LUXEMBOURG'),
  createData('RECKITT BENCKISER GROUP PLC', '3.37%', 'Activités des services financiers'),
  createData('AIR LIQUIDE', '3.34%', 'Industrie chimique'),
  createData('DNCA FINANCE LUXEMBOURG', '3.24%', 'DNCA FINANCE LUXEMBOURG'),
  createData('AIR LIQUIDE', '3.34%', 'Industrie chimique'),
  createData('DNCA FINANCE LUXEMBOURG', '3.24%', 'DNCA FINANCE LUXEMBOURG'),
  createData('RECKITT BENCKISER GROUP PLC', '3.37%', 'Activités des services financiers'),
  createData('AIR LIQUIDE', '3.34%', 'Industrie chimique'),
  createData('DNCA FINANCE LUXEMBOURG', '3.24%', 'DNCA FINANCE LUXEMBOURG'),
  createData('RECKITT BENCKISER GROUP PLC', '3.37%', 'Activités des services financiers'),
];







/*-------------------------------------------------------------------------*/
export default function App() {

  const [property, setProperty] = useState('wb_a2')


  const handelClick =(label) =>{
    console.log(label);
  }

  const classes = useStyles();

  return (
    <div>
      <Header />

      <Grid className={classes.root} direction="row" justify="center" alignItems="center" container spacing={1}>
        <Grid item xs={12} className={classes.item}  >
          <Paper />
        </Grid>
        <Grid item xs={12} sm={10} md={5} lg={3} >
          <Box Tittle={'Classes d\'Actifs'} content={<DoughnutChart data ={[30, 5, 10, 5]} labels={['Action','Obligation','Futures','Fonds']}  handelClick={handelClick}  />} />
        </Grid>
        <Grid item xs={12} sm={10} md={5} lg={6} >
          <Box Tittle={'Top 5 des secteurs d\'Activités'} content={<Barchart data = {[40.6, 35.3, 28.1, 26.7, 22.8]} labels= {['Services financiers & assurance','Production et distribution d\'électricité','Édition','Fabrication de produits informatiques','Industrie chimique']} />} />
        </Grid>
        <Grid item xs={12} sm={10} md={5} lg={3} >
          <Box Tittle={'SCR Marché'} content ={ <ScrRatingChart SCRMarche={30} SCRAction={50} SCRTaux={20} SCRDevise={80} SCRContrepartie={90}/> }/>
        </Grid>
        {/* 2eme ligne*/}

        <Grid item xs={12} sm={10} md={5} lg={3} >
          <Box Tittle={'Poids par Rating'} content={<RatingBarCahrt data= {[0, 40.6, 35.3, 28.1, 26.7, 22.8,50]} labels={['aaa','aa','a','bbb','bb','b','nr']} />} />
        </Grid>
        <Grid item xs={12} sm={10} md={5} lg={6} >
          <Box Tittle={'Exposition  Geographique'} content={<GeoChart data={Geodata} property ={property} countries ={{'FR' : 'FR','AU': 'AU','BG': 'GB', 'US' : 'US'}}/>}/>
        </Grid>
        <Grid item xs={12} sm={10} md={5} lg={3} >
          <Box Tittle={'ESG & Carbon'} />
        </Grid>

        {/* 2eme ligne*/}

        <Grid item xs={12} sm={10} md={5} lg={3} >
          <Box Tittle={'Maturité Moyenne'} content={<MaturityBarChart data={[0, 40.6, 35.3, 28.1, 26.7, 22.8,50]} labels={['1','2','3','4','5','6','>20' ]}/>} />
        </Grid>
        <Grid item xs={12} sm={10} md={5} lg={6} >
          <Box Tittle={'Contreparties'} content ={<CountrepartieTable data ={rows}/>}/>
        </Grid>
        <Grid item xs={12} sm={10} md={5} lg={3} >
          <Box Tittle={'Risque Climatique'} />
        </Grid>
      </Grid>


    </div>
  );

}
