import React, { Component } from 'react';
import { Bar } from 'react-chartjs-2';

class BarChart extends Component {


    state = {
        data: {
            datasets: [{
                barPercentage: 0.2,

                minBarLength: 2,
                data: [0, 40.6, 35.3, 28.1, 26.7, 22.8,50],
                backgroundColor: [
                    '#B6C512',
                    '#B6C512',
                    '#B6C512',
                    '#B6C512',
                    '#B6C512',
                    '#B6C512',
                    '#B6C512',

                    
                ],
                borderWidth: 0
            }],

            // These labels appear in the legend and in the tooltips when hovering different arcs
            labels: [
                '1',
                '2',
                '3',
                '4',
                '5',
                '6',
                '>20'
                
            ],
            

        }

    }

    // constructor(props) {
    //     super(props);

    //     this.state.data.datasets[0]. = props.chartData;
    // }
    render() {
        return (
            <div className="chart-container" style={{ position: 'relative', height: '100%', width: '100%' }}>
                <Bar

                    data={this.state.data}
                    width={'70%'}
                    height={'35%'}
                    options={{
                        maintainAspectRatio: false,

                        legend: {

                            display: false,
                            labels: {
                                // This more specific font property overrides the global property
                                fontColor: 'red',
                                boxWidth: 10,
                                padding: 20,
                                fontFamily: 'Helvetica Neue',

                            }
                        },

                        layout: {
                            padding: {
                                left: 10,
                                right: 10,
                                top: 20,
                                bottom: 40
                            }
                        },
                        scales: {
                            xAxes: [{
                                gridLines: {
                                    display:false,
                                    
                                },
                                ticks: {
                                    fontColor: '#edf0f1',
                                    fontSize: 12,
                                    fontFamily:'Helvetica Neue',
                                    stepSize: 10,
                                    beginAtZero: true
                                }
                            }],
                            yAxes: [{
                                gridLines: {
                                    
                                    display:true,
                                    color:'rgb(217,217,217)',
                                    lineWidth:0.5,
                                    zeroLineWidth:0,
                                    zeroLineColor :'rgb(30,72,125)',
                                    drawBorder: false,
                                    borderDash:[5,5]
                                   
                                } ,
                                ticks: {
                                    fontColor: '#edf0f1',
                                    fontSize: 15,
                                    fontFamily:'Helvetica Neue',
                                    stepSize: 5,
                                    beginAtZero: true
                                }  
                            }]
                        }

                    }
                    }
                />
            </div>
        );
    }
}

export default BarChart;
