    Object.defineProperty(jsonCountriesArray, row, {
      configurable: false,
      enumerable: false,
      value: row,
      writable: true
    });
  }, {})
  return jsonCountriesArray
}

/*-------------------------------------------------------------------------*/
export default function App() {

  const [property, setProperty] = useState('wb_a2');
  const [dataActifs, setDataActifs] = useState({ data: [0, 0, 0], labels: ['', '', ''] });
