import React, { Component, useRef, useEffect, useState } from 'react';
import {select,selectAll, geoMercator,  geoPath, min , max , scaleLinear} from 'd3'
import useResizeObserver from "use-resize-observer";


function GeoChart({ data, property }) {
    const svgRef = useRef();
    const wrapperRef = useRef();
    const dimensions = useResizeObserver(wrapperRef);
    const [selectedCountry, setSelectedCountry] =useState(null);


    useEffect(()=>{
        const svg = select(svgRef.current);
        const {width, height} = dimensions || wrapperRef.current.getBoudingClientRect();
        const projection = geoMercator().fitSize([680,270], 
            selectedCountry || data);
        const geoPathGenerator = geoPath().projection(projection);
        
        // const minProp = min(data.features, feature=>feature.properties[property])
        // const maxProp = max(data.features, feature=>feature.properties[property])
        // const colorScale = scaleLinear().domain([minProp, maxProp]).range(["#ccc","red"]);
        

        svg
        .selectAll(".country")
        .data(data.features).join('path')
        .on('click', feature=> console.log(feature))
        .attr('class','country')
        .transition()
        .attr('fill', feature => feature.properties[property]==="FR" ? 'red' :'#ccc')
        .attr('d',feature=> geoPathGenerator(feature));

    }, [data, dimensions, property ]);


    return (
    <div ref={wrapperRef} style={{margin:'5px 0px 0px 0px', width:'100%',  height:'100%'}}  > 
        <svg ref={svgRef} style={{margin:'0px 0px 0px 0px', width:'100%',  height:'100%'}}></svg>
    </div>
    );

}

export default GeoChart;
