import React, { Component, useRef, useEffect } from 'react';
import {select,selectAll, geoMercator, geoPath} from 'react-d3-library'
import useResizeObserver from "use-resize-observer";


function GeoChart({ data, property }) {
    const svgRef = useRef();
    const wrapperRef = useRef();
    const dimensions = useResizeObserver(wrapperRef);

    useEffect(()=>{
       // const svg = select(svgRef.current);
        const {width, height} = dimensions;
        const projection = geoMercator();
        const geoPathGenerator = geoPath().projection(projection);

        svg.selectAll(".country").data(data.features).join('path').attr('class','country').attr('d',feature=> geoPathGenerator(feature));

    }, [data, dimensions, property ]);


    return (
    <div ref={wrapperRef} style={{margin:'2rem'}}  > 
        <svg ref={svgRef}></svg>
    </div>
    );

}

export default GeoChart;
