import React, {useState, useEffect} from 'react';
import Header from './Header/Header'
import AppStyle from './App.css';
import Paper from './Filtres/FIltre'
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Box from './Box/Box';
import Chart from './Chart/DoughnutChart'
import Barchart from './Chart/BarChart'

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    maxWidth: '1500px',
    marginRight:'auto',
    marginLeft:'auto',
  },
  item:{
    minWidth:'300px',
  }
}));

export default function App() {

  const [data1, setData1] = useState([])
  const [data2, setData2] = useState([])
  const classes = useStyles();
  useEffect(() => {
    setData1({
       data1: {
        datasets: [{
            barPercentage: 0.5,

            minBarLength: 2,
            dataa: [],
            backgroundColor: [],
            borderWidth: 0
        }],
        labels: ['Services financiers & assurance','Production et distribution d\'électricité','Édition','Fabrication de produits informatiques','Industrie chimique'],
    }
    }, [])

    setData2({
      data2: {
        datasets: [{
            barPercentage: 0.5,

            minBarLength: 2,
            dataa: [],
            backgroundColor: [],
            borderWidth: 0
        }],
        labels: ['Services financiers & assurance','Production et distribution d\'électricité','Édition','Fabrication de produits informatiques','Industrie chimique'],
    }
    },[])

  })
  return (
    <div>
      <Header />

        <Grid className={classes.root} direction="row" justify="center" alignItems="center" container spacing={1}>
          <Grid item xs={12} className={classes.item}  >
            <Paper/>
          </Grid>
           <Grid item xs={12} sm={10} md={5} lg={3} >
            <Box Tittle={'Classes d\'Actifs'} content={<Chart/>}/>
          </Grid>
          <Grid item xs={12} sm={10} md={5} lg={6} >
            <Box Tittle={'Top 5 des secteurs d\'Activités'} 
            content={<Barchart 
                      data ={data1} />
                      }/>
          </Grid>
          <Grid item xs={12} sm={10} md={5} lg={3} >
            <Box Tittle={'SCR MARCHE'}/>
          </Grid>
          {/* 2eme ligne*/}

           <Grid item xs={12} sm={10} md={5} lg={3} >
            <Box Tittle={'Poids par Rating'} 
            content={<Barchart 
              data ={data2}/>          
            }/>
          </Grid>
          <Grid item xs={12} sm={10} md={5} lg={6} >
            <Box Tittle={'Exposition  Geographique'} />
          </Grid>
          <Grid item xs={12} sm={10}  md={5} lg={3} >
            <Box Tittle={'ESG & Carbon'}/>
          </Grid>
          {/* 2eme ligne*/}

          <Grid item xs={12} sm={10} md={5} lg={3} >
            <Box Tittle={'Maturité Moyenne'}/>
          </Grid>
          <Grid item xs={12} sm={10} md={5} lg={6} >
            <Box Tittle={'Contreparties'}/>
          </Grid>
          <Grid item xs={12} sm={10} md={5} lg={3} >
            <Box  Tittle={'Risque Climatique'}/>
          </Grid>
        </Grid>


    </div>
  );

}
