import React, { Component } from 'react';
import { HorizontalBar } from 'react-chartjs-2';

class BarChart extends Component {


    state = {
        data: {
            datasets: [{
                barPercentage: 0.7,

                minBarLength: 2,
                data: [40.6, 35.3, 28.1, 26.7, 22.8],
                backgroundColor: [
                    'rgb(30,72,125)',
                    'rgb(47,85,151)',
                    'rgb(143,170,220)',
                    'rgb(180,199,231)',
                    'rgb(218,227,243)'
                ],
                borderWidth: 0
            }],

            // These labels appear in the legend and in the tooltips when hovering different arcs
            labels: [
                'Services financiers & assurance',
                'Production et distribution d\'électricité',
                'Édition',
                'Fabrication de produits informatiques',
                'Industrie chimique'
            ]

        }

    }

    // constructor(props) {
    //     super(props);

    //     this.state.chartData = props.chartData;
    // }
    render() {
        return (
            <div className="chart-container" style={{ position: 'relative', height: '100%', width: '100%' }}>
                <HorizontalBar

                    data={this.state.data}
                    width={'70%'}
                    height={'35%'}
                    options={{
                        maintainAspectRatio: false,

                        legend: {
                            display: false,
                            labels: {
                                // This more specific font property overrides the global property
                                fontColor: '#edf0f1',
                                boxWidth: 10,
                                padding: 20,
                                fontFamily: 'Helvetica Neue',

                            }
                        },

                        layout: {
                            padding: {
                                left: 0,
                                right: 0,
                                top: 0,
                                bottom: 40
                            }
                        },
                        scales:{
                            gridLines:{
                                display:true,
                                color: 'rgba(0, 0, 0, 0.1)'
                            }
                        }

                    }
                    }
                />
            </div>
        );
    }
}

export default BarChart;
